def speed_labels(bins, units):
    """
    Generates labels for windspeed bins

    Input
    ----------
    bins: Windspeed bins
    units: = Unit
    """
    labels = []
    for left, right in zip(bins[:-1], bins[1:]):
        if left == bins[0]:
            labels.append('calm'.format(right))
        elif np.isinf(right):
            labels.append('>{} {}'.format(left, units))
        else:
            labels.append('{} - {} {}'.format(left, right, units))

    return list(labels)

